        int b = 0;

        //添加空格
        int j = 1;
        while(j <= i)
        {
            cout << " ";
            j++;
        }

        while(b <= 2*(a - i) - 2)
        {
            //b:每行输出次数
            cout << "#";
            b++;
        }
    cout <<endl;
    }
